import React,{useState} from 'react';
//import { makeStyles } from "@mui/styles";
import { Button, Modal, TextField,Grid,Paper } from '@material-ui/core';


export default function Edit() {
  //  const classes=useStyles();

     const [open, setOpen] = useState(false);

    const [orderCurrency, setOrderCurrency] = useState("");
    const [distributionChannel, setDistributionChannel] = useState("");
    const [companyCode, setCompanyCode] = useState("");
  
    const handleOpen = () => {
      setOpen(true);
    };
  
    const handleClose = () => {
      setOpen(false);
    };
  
    const handleSave = () => {
      // Perform save logic here
      handleClose();
    };

  return (

        <div>
      <Button variant="contained" onClick={handleOpen} style={{
              color: "white",
              backgroundColor: "#fc7500",
              marginRight: "10px",
            }}>
        Edit 
      </Button>

      <Modal open={open} onClose={handleClose}>
        <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', width: 400, backgroundColor: '#fff',border: '3px solid black', padding: 16 }}>
        <span style={{ color: "Black", fontSize: "20px"}}>
          Edit
        </span>
        <div className="form-container" > 
      
        <Grid
          container
          spacing={2}
          sx={{ paddingTop: "30px", marginLeft: "5px" }}
        >
          <Grid item xs={6}>
          <TextField style={{marginTop:"10px"}}
              id="order_currency"
              label="ORDER CURRENCY"
              variant="outlined"
              sx={{ background: "white", borderRadius: "10px", width: "100%" }}
              value={orderCurrency}
              onChange={(newValue) => setOrderCurrency(newValue.target.value)}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField style={{marginTop:"10px"}}
              id="company_code"
              label="COMPANY CODE"
              variant="outlined"
              sx={{ background: "white", borderRadius: "10px", width: "100%" }}
              value={companyCode}
              onChange={(newValue) =>
                setCompanyCode(newValue.target.value)
              }
            />
          </Grid>
          <Grid item xs={12}>
            <TextField style={{width:"100%"}}
              id="distribution_channel"
              label="DISTRIBUTION CHANNEL"
              variant="outlined"
             sx={{ background: "white", width: "100%" }}
              value={distributionChannel}
              onChange={(newValue) => setDistributionChannel(newValue.target.value)}
            />
          </Grid>
        </Grid>

          <Button variant="contained" color="primary" onClick={handleSave} style={{color: "black",border:"1px solid black",backgroundColor:"white",marginRight: "10px",marginTop:"9px",width:"45%" }}>
            Edit
          </Button>
          <Button variant="contained" color="secondary" onClick={handleClose} style={{color: "black",border:"1px solid black",backgroundColor:"white",marginRight: "10px",marginTop:"9px",width:"50%" }}>
            Cancel
          </Button>
        </div>
        </div>
      </Modal>
    </div>
  );
}
