import React, { useEffect } from 'react';
import Highcharts from 'highcharts';
import axios from 'axios';

const Pie = () => {
    useEffect(() => {
        // Make monochrome colors
        const colors = Highcharts.getOptions().colors.map((i, c) =>
          Highcharts.color('#8fd163')
            .brighten((i - 3) / 3)
            .get()
        );
    
        // Build the chart
        Highcharts.chart('PieChart', {
          chart: {
            plotBackgroundColor: null,
            plotBorderWidth: null,
            plotShadow: false,
            backgroundColor: 'Transparent',
            type: 'pie',
          },
          title: {
            text: 'Open and Closed invoices',
            align: 'center',
            style: {color: 'white',},
          },
          tooltip: {
            pointFormat: '{series.name}: <b>{point.percentage:.1f}%</b>',
          },
          accessibility: {
            point: {
              valueSuffix: '%',
            },
          },
          plotOptions: {
            pie: {
                allowPointSelect: true,
                cursor: 'pointer',
                borderRadius: 5,
                colors,
                dataLabels: {
                    enabled: true,
                    format: '<b>{point.name}</b><br>{point.percentage:.1f} %',
                    distance: -50,
                    filter: {
                        property: 'percentage',
                        operator: '>',
                        value: 4
                    }
                }
            }
        },
          series: [
            {
              name: 'Share',
              data: [ 
              { name: 'Open', y: 74.03 },
              { name: 'Close', y: 12.66 },],
            },
          ],
        });
      }, []);
  
    return(
      <div id="PieChart"></div> 
    ); 
  };
  
export default Pie;
