import { useState } from "react";
//import { useEffect } from "react";
//import { getData, keys } from "./services/data";
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import TableMUI from './cmpnts/TableMUI';
import AddData from './cmpnts/add_data';
import Ctfoot  from './cmpnts/Ctfoot';
import Headers from './cmpnts/Headers';
import AnalyticsView from "./cmpnts/AnalyticsView";
import SearchResult from "./cmpnts/SearchResults";
//import Add_Data from '.components/AddButton';


function TabPanel({ children, value, index }) {
  return (
    <div hidden={value !== index}>
      {value === index && (
        <Typography component="div" style={{ padding: 8 }}>
          {children}
        </Typography>
      )}
    </div>
  );
}

const useStyles = makeStyles(theme => ({
  indicator: {
    backgroundColor: 'white', // Replace 'red' with your desired color
  },
}));

function App() {
  const [value, setValue] = useState(0);
  const [showHiddenTab, setShowHiddenTab] = useState(false);
  //const [ad_State, setAddDataState] = useState(false);
  //const [data, setData] = useState([]);

  //useEffect(async () => {
  //  setData(await getData());
  //}, []);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleButtonClick = () => {
    setShowHiddenTab(true);
    setValue(3);
  };

  //const handleAddData = () => {
  //  setAddDataState(!ad_State);
  //  console.log("addData state changed", ad_State);
  //};

  return (
    <div>
      <Headers />
    <div style={{ flexGrow: 1, backgroundColor: '#666666', height: '100vh' }}>
      <Tabs value={value} classes={{ indicator: classes.indicator }} onChange={handleChange}>
        <Tab label="Home Page" 
        style={{ minWidth: 80, fontSize: 12, backgroundColor: '#666666', color: 'white' }}/>
        <Tab label="Add Data" 
        style={{ minWidth: 80, fontSize: 12, backgroundColor: '#666666', color: 'white' }}/>
        <Tab label="Analytics View" 
        style={{ minWidth: 80, fontSize: 12, backgroundColor: '#666666', color: 'white' }}/>
        {showHiddenTab ? (
          <Tab label="Search Result" 
          style={{ minWidth: 80, fontSize: 12, backgroundColor: '#666666', color: 'white' }}/>
        ) : (
          <Button variant="contained"  onClick={handleButtonClick}>
            Show Hidden Tab
          </Button>
        )}

      </Tabs>
      <TabPanel value={value} index={0}>
        <TableMUI />
      </TabPanel>
      <TabPanel value={value} index={1}>
        <AddData />
      </TabPanel>
      <TabPanel value={value} index={2}>
        <AnalyticsView />
      </TabPanel>
    </div >
    <div style={{backgroundColor: 'white', marginUpper: '100px', paddingTop: '100px', }}>
      <Ctfoot />
    </div>
    </div>
  );
}

export default App;
