import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';

function MyForm() {
  const [formData, setFormData] = useState({
    sl_no: '',
    customer_order_id: '',
    sales_org: '',
    distribution_channel: '',
    company_code: '',
    order_creation_date: '',
    order_amount: '',
    order_currency: '',
    customer_number: '',
    amount_in_usd: '',
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Perform actions with form data
    console.log(formData);
  };

  const handleCancel = () => {
    setFormData({
      sl_no: '',
      customer_order_id: '',
      sales_org: '',
      distribution_channel: '',
      company_code: '',
      order_creation_date: '',
      order_amount: '',
      order_currency: '',
      customer_number: '',
      amount_in_usd: '',
    });
  };

  const inputStyle = {
    backgroundColor: 'transparent',
    border: 'none',
    borderBottom: '1px solid #000',
    marginBottom: '5px', // Added margin of 5px
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'row',
    flexWrap: 'wrap',
  };

  const divStyle = {
    width: '50%',
  };

  const datePickerStyle = {
    '& .MuiInputBase-root': {
      display: 'none',
    },
    '& .MuiInputBase-input[type="date"]': {
      display: 'block',
    },
  };

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <div style={divStyle}>
        <TextField
          label="customer_order_id"
          name="customer_order_id"
          value={formData.customer_order_id}
          onChange={handleChange}
          variant="outlined"
          style={inputStyle}
        />
        <TextField
          label="sales_org"
          name="sales_org"
          value={formData.sales_org}
          onChange={handleChange}
          variant="outlined"
          style={inputStyle}
        />
      </div>
      <div style={divStyle}>
        <TextField
          label="distribution_channel"
          name="distribution_channel"
          value={formData.distribution_channel}
          onChange={handleChange}
          variant="outlined"
          style={inputStyle}
        />
      </div>
      <div style={divStyle}>
        <TextField
          label="customer_number"
          name="customer_number"
          value={formData.customer_number}
          onChange={handleChange}
          variant="outlined"
          style={inputStyle}
        />
        <TextField
          label="company_code"
          name="company_code"
          value={formData.company_code}
          onChange={handleChange}
          variant="outlined"
          style={inputStyle}
        />
      </div>
      <div style={divStyle}>
        <TextField
          label="order_currency"
          name="order_currency"
          value={formData.order_currency}
          onChange={handleChange}
          variant="outlined"
          style={inputStyle}
        />
        <TextField
          label="amount_in_usd"
          name="amount_in_usd"
          value={formData.amount_in_usd}
          onChange={handleChange}
          variant="outlined"
          style={inputStyle}
        />
        <TextField
          label="order_creation_date"
          name="order_creation_date"
          type="date"
          value={formData.order_creation_date}
          onChange={handleChange}
          InputLabelProps={{
            shrink: true,
          }}
          variant="outlined"
          style={inputStyle}
          className={datePickerStyle}
        />
      </div>
      <div>
        <button type="submit">Submit</button>
        <button type="button" onClick={handleCancel}>Cancel</button>
      </div>
    </form>
  );
}

export default MyForm;
