import React, { useEffect } from 'react';
import Highcharts from 'highcharts';

const AnalyticsView = () => {
  useEffect(() => {
    // Make monochrome colors
    const colors = Highcharts.getOptions().colors.map((i, c) =>
      Highcharts.color('#8fd163')
        .brighten((i - 3) / 3)
        .get()
    );

    // Build the chart
    Highcharts.chart('chartContainer', {
      chart: {
        plotBackgroundColor: null,
        plotBorderWidth: null,
        plotShadow: false,
        backgroundColor: 'Transparent',
        type: 'pie',
      },
      title: {
        color: 'white',
        text: 'Open and Closed invoices',
        align: 'center',
      },
      tooltip: {
        pointFormat: '{series.name}: <b>{point.percentage:.1f}%</b>',
      },
      accessibility: {
        point: {
          valueSuffix: '%',
        },
      },
      plotOptions: {
        pie: {
            allowPointSelect: true,
            cursor: 'pointer',
            borderRadius: 5,
            colors,
            dataLabels: {
                enabled: true,
                format: '<b>{point.name}</b><br>{point.percentage:.1f} %',
                distance: -50,
                filter: {
                    property: 'percentage',
                    operator: '>',
                    value: 4
                }
            }
        }
    },
      series: [
        {
          name: 'Share',
          data: [ 
          { name: 'Open', y: 74.03 },
          { name: 'Close', y: 12.66 },],
        },
      ],
    });
  }, []);

  const squareStyle = {
    width: '30%',
    height: '30%',
    backgroundColor: '#666666',
    margin: '10px',
  };

  return (
    <div style={{ display: 'flex' }}>
      <div style={squareStyle}></div>
      <div style={squareStyle}></div>
      <div id="chartContainer" style={squareStyle}></div>
    </div>
  );
};

export default AnalyticsView;
