import React, { useEffect } from 'react';
import Highcharts from 'highcharts';
import ChartInput from './ChartInput';
import Pie from './PieChart';

const AnalyticsView = () => {

  useEffect(() => {
    Highcharts.chart('BarGraph', {
      chart: {
        type: 'column',
        square: true,
        backgroundColor: 'Transparent',
      },
      title: {
        align: 'center',
        text: 'Total Amount Per Distribution Channel',
        style: {
            color: 'white',
          },
      },
      xAxis: {
        categories: ['Component'],
        visible: true,
        labels: {
            style: {
              color: 'white',
            },
          },
      },
      yAxis: {
        title: {
          text: 'Value',
          style: {
            color: 'white',
          },
        },
        labels: {
            style: {
              color: 'white',
            },
          },
      },
      series: [
        {
          name: 'Value',
          data: [102000],
          dataLabels: {
            enabled: true,
            color: 'white',
          },
        },
      ],
    });
  }, []);

  const squareStyle = {
    width: '30%',
    height: '30%',
    backgroundColor: '#666666',
    margin: '10px',
  };

  return (
    <div style={{ display: 'flex', backgroundColor: '#666666', }}>
      <div style={squareStyle}>
        <ChartInput />
      </div>
      <div id="BarGraph" style={squareStyle}></div>
      <div style={squareStyle}>
        <Pie />
      </div>
    </div>
  );
};

export default AnalyticsView;
