import React, { useState } from 'react';
import axios from 'axios';
import TextField from '@material-ui/core/TextField';

const ChartInput = () => {
  const [firstField, setFirstField] = useState('');
  const [secondField, setSecondField] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/your-backend-api-url', {
        distributionChannel: firstField,
        customerNumber: secondField,
      });

      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div style={{ border: '2px solid white', padding: '10px',  borderRadius: '10px'  }}>
      <form onSubmit={handleSubmit}>
        <TextField
          variant="outlined"
          label="Distribution Channel"
          value={firstField}
          onChange={(e) => setFirstField(e.target.value)}
          fullWidth
          style={{ marginBottom: '10px', backgroundColor: 'white',  borderRadius: '5px'  }}
        />
        <TextField
          variant="outlined"
          label="Customer Number"
          value={secondField}
          onChange={(e) => setSecondField(e.target.value)}
          fullWidth
          style={{ marginBottom: '10px', backgroundColor: 'white', borderRadius: '5px' }}
        />
        <div style={{ display: 'flex', justifyContent: 'center', height: '200%' }}>
          <button 
          type="submit" 
          style={{ 
            width: '100%',
              height: '40px',
              backgroundColor: 'transparent',
              border: '1px solid white',
              color: 'white',
              borderRadius: '5px',
            }}>
                View
            </button>
        </div>
      </form>
    </div>
  );
};

export default ChartInput;
