import { useState } from "react";
import { useEffect } from "react";
import { getData, keys } from "./services/data";
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import TableMUI from './components/TableMUI';
import Add_Data from '.components/AddButton';


function TabPanel({ children, value, index }) {
  return (
    <div hidden={value !== index}>
      {value === index && (
        <Typography component="div" style={{ padding: 8 }}>
          {children}
        </Typography>
      )}
    </div>
  );
}

function App() {
  const [value, setValue] = useState(0);
  const [showHiddenTab, setShowHiddenTab] = useState(false);
  const [ad_State, setAddDataState] = useState(false);
  const [data, setData] = useState([]);

  useEffect(async () => {
    setData(await getData());
  }, []);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleButtonClick = () => {
    setShowHiddenTab(true);
    setValue(3);
  };

  const handleAddData = () => {
    setAddDataState(!ad_State);
    console.log("addData state changed", ad_State);
  };

  return (

    <div style={{ flexGrow: 1, backgroundColor: '#f5f5f5', height: '100vh' }}>
      <Tabs value={value} onChange={handleChange}>
        <Tab label="Home Page" />
        <Tab label="Add Data" />
        {showHiddenTab ? (
          <Tab label="Search Result" />
        ) : (
          <Button variant="contained" color="primary" onClick={handleButtonClick}>
            Show Hidden Tab
          </Button>
        )}
        <Tab label="Analytics View" />

      </Tabs>
      <TabPanel value={value} index={0}>
        <TableMUI />
      </TabPanel>
      <TabPanel value={value} index={1}>
        <React.Fragment>
        {ad_State && (
          <Add_Data
            stateHandler={setAddDataState}
            trigger={ad_State}
            handler={handleAddData}
          />
        )}
        </React.Fragment>
      </TabPanel>
      <TabPanel value={value} index={2}>
        Analytics View Pannel
      </TabPanel>
    </div>
  );
}

export default App;
