import React, { useEffect } from 'react';
import Highcharts from 'highcharts';

const AnalyticsView = () => {
  useEffect(() => {
    // Make monochrome colors
    const colors = Highcharts.getOptions().colors.map((c, i) =>
      Highcharts.color(Highcharts.getOptions().colors[0])
        .brighten((i - 3) / 7)
        .get()
    );

    // Build the chart
    Highcharts.chart('chartContainer', {
      chart: {
        plotBackgroundColor: null,
        plotBorderWidth: null,
        plotShadow: false,
        type: 'pie',
      },
      title: {
        text: 'Open and Closed invoices',
        align: 'center',
      },
      tooltip: {
        pointFormat: '{series.name}: <b>{point.percentage:.1f}%</b>',
      },
      accessibility: {
        point: {
          valueSuffix: '%',
        },
      },
      plotOptions: {
        pie: {
            allowPointSelect: true,
            cursor: 'pointer',
            dataLabels: {
                enabled: true,
                format: '<b>{point.name}</b>: {point.percentage:.1f} %'
            }
        }
    },
      series: [
        {
          name: 'Share',
          data: [ { name: 'Chrome', y: 74.03 },
          { name: 'Edge', y: 12.66 },
          { name: 'Firefox', y: 4.96 },
          { name: 'Safari', y: 2.49 },
          { name: 'Internet Explorer', y: 2.31 },
          { name: 'Other', y: 3.398 } ],
        },
      ],
    });
  }, []);

  const squareStyle = {
    width: '30%',
    height: '30%',
    backgroundColor: 'red',
    margin: '10px',
  };

  return (
    <div style={{ display: 'flex' }}>
      <div style={squareStyle}></div>
      <div style={squareStyle}></div>
      <div id="chartContainer" style={squareStyle}></div>
    </div>
  );
};

export default AnalyticsView;


// Data retrieved from https://netmarketshare.com
Highcharts.chart('container', {
    chart: {
        plotBackgroundColor: null,
        plotBorderWidth: null,
        plotShadow: false,
        type: 'pie'
    },
    title: {
        text: 'Browser market shares in May, 2020',
        align: 'left'
    },
    tooltip: {
        pointFormat: '{series.name}: <b>{point.percentage:.1f}%</b>'
    },
    accessibility: {
        point: {
            valueSuffix: '%'
        }
    },
    plotOptions: {
        pie: {
            allowPointSelect: true,
            cursor: 'pointer',
            dataLabels: {
                enabled: true,
                format: '<b>{point.name}</b>: {point.percentage:.1f} %'
            }
        }
    },
    series: [{
        name: 'Share',
        colorByPoint: true,
        data: []
    }]
});
